import React, { useContext } from "react";
import { Container, Typography, Grid, Paper, Avatar, Box } from "@mui/material";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext"; // Importing authentication context

const Dashboard = () => {
  const { user } = useContext(AuthContext); // Getting logged-in user details
  const role = user?.role || "user"; // Default role is "user" if not specified

  // Define dashboard items based on roles
  const dashboardItems = {
    user: [
      { title: "ü©∫ MediScan", link: "/medicine-verification" },
      { title: "üìú My Prescription", link: "/my-prescriptions" },
      { title: "üö® Report Fraud", link: "/complaint" },
      { title: "üè¨ Best Store #1", link: "/top-stores" },
      { title: "üè¨ Best Store #2", link: "/top-stores" },
      { title: "üè¨ Best Store #3", link: "/top-stores" },
    ],
    doctor: [
      { title: "üìã Add Prescription", link: "/add-prescription" },
      { title: "üîç Verify Medicines", link: "/medicine-verification" },
      { title: "ü©∫ Patient Reports", link: "/patient-reports" },
    ],
    pharmacist: [
      { title: "üìú View Prescriptions", link: "/pharmacy-access" },
      { title: "üíä Verify Medicine Stock", link: "/medicine-stock" },
    ],
    routeUser: [
      { title: "‚úîÔ∏è Validate Complaints", link: "/validate-complaints" },
      { title: "üìú Generate Barcodes", link: "/generate-barcodes" },
      { title: "üè≠ Issue Licenses", link: "/issue-licenses" },
    ],
    manufacturer: [
      { title: "üè≠ View Barcodes", link: "/view-barcodes" },
      { title: "üìù Manage Production", link: "/manage-production" },
    ],
    admin: [
      { title: "üë• Manage Users", link: "/manage-users" },
      { title: "üìä System Reports", link: "/system-reports" },
    ],
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      {/* Profile Banner */}
      <Paper elevation={3} sx={{ p: 3, display: "flex", alignItems: "center", gap: 2 }}>
        <Avatar sx={{ width: 60, height: 60, bgcolor: "#2E3B55" }}>{role.charAt(0).toUpperCase()}</Avatar>
        <Box>
          <Typography variant="h6">Welcome, {user?.name || "User"}</Typography>
          <Typography variant="body2" color="textSecondary">
            Role: {role.charAt(0).toUpperCase() + role.slice(1)}
          </Typography>
        </Box>
      </Paper>

      {/* Feature Boxes (Dynamic Based on Role) */}
      <Grid container spacing={3} sx={{ mt: 3 }}>
        {dashboardItems[role].map((item, index) => (
          <Grid item xs={6} md={4} key={index}>
            <Link to={item.link} style={{ textDecoration: "none" }}>
              <Paper elevation={3} sx={boxStyle}>
                <Typography variant="h6">{item.title}</Typography>
              </Paper>
            </Link>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

// Box styling
const boxStyle = {
  p: 3,
  textAlign: "center",
  bgcolor: "#f5f5f5",
  "&:hover": { bgcolor: "#e0e0e0", cursor: "pointer" },
};

export default Dashboard;
